import { useState } from "react";
import { useNavigate } from "react-router-dom";
import styled from "styled-components";
import { motion } from "framer-motion";
import { AccountContext } from "./marginer/accountContext";
import { LoginForm } from "./forms/loginForm";
import { SignupForm } from "./forms/signupForm";
import { login } from "./Backend/autenticar";
import { registrar } from "./Backend/cadastrar";

// Uso de Style-comnponents para estilização da tela de login
const BoxContainer = styled.div`
  width: 280px;
  min-height: 550px;
  display: flex;
  flex-direction: column;
  border-radius: 19px;
  background-color: #fff;
  box-shadow: 0 0 2px rgba(15, 15, 15, 0.28);
  position: relative;
  overflow: hidden;
`;

const TopContainer = styled.div`
  width: 100%;
  height: 250px;
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
  padding: 0 1.8em;
  padding-bottom: 5em;
`;

const BackDrop = styled(motion.div)`
  position: absolute;
  width: 160%;
  height: 550px;
  display: flex;
  flex-direction: column;
  border-radius: 50%;
  top: -290px;
  left: -70px;
  transform: rotate(60deg);
  background: linear-gradient(58deg, #f7d600 20%, rgba(241, 196, 15, 1) 100%);
`;

const HeaderContainer = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
`;

const HeaderText = styled.div`
  font-size: 30px;
  font-weight: 600;
  line-height: 1.24;
  color: #fff;
  z-index: 10;
`;

const SmallText = styled.div`
  font-size: 11px;
  font-weight: 500;
  color: #fff;
  margin-top: 7px;
  z-index: 10;
`;

const InnerContainer = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  padding: 0px 20px;
`;

const backdropVariants = {
  expanded: {
    width: "233%",
    height: "1050px",
    borderRadius: "20%",
    transform: "rotate(60deg)",
  },
  collapsed: {
    width: "160%",
    height: "550px",
    borderRadius: "50%",
    transform: "rotate(60deg)",
  },
};

const expandingTransition = {
  type: "spring",
  duration: 2.3,
  stiffness: 30,
};

//fim da estilização usando o styles-components

export default function SignIn(props) {
  //uso do UseState para uma transição de um formulario para o outro
  const [isExpanded, setExpanded] = useState(false);
  const [active, setActive] = useState("signin");
  // uso do navigate para apos a validação do usuario navegue para a homePage
  const navigate = useNavigate();

  //Função para envio dos dados de login para validação ao Banco de dados
  const handleSubmit = async (email, password) => {
    login(email, password, navigate);
  };

  //Função para envio dos dados de cadastro para inserção ao Banco de dados
  const handleRegistroSubmit = (name, email, password) => {
    registrar(name, email, password);
  };
  //Funçao de animação de mudança de formulario
  const playExpandingAnimation = () => {
    setExpanded(true);
    setTimeout(() => {
      setExpanded(false);
    }, expandingTransition.duration * 750);
  };
  //troca entre sigin e sigup
  const switchToSignup = () => {
    playExpandingAnimation();
    setTimeout(() => {
      setActive("signup");
    }, 400);
  };
  //troca entre sigin e sigup
  const switchToSignin = () => {
    playExpandingAnimation();
    setTimeout(() => {
      setActive("signin");
    }, 400);
  };

  const contextValue = { switchToSignup, switchToSignin };

  return (
    <>
      {/* Logo da soulcode  */}
      <div>
        <img
          src="data:image/png;base64,"
          alt="SoulCode"
          width="120px"
          height="120px"
        />
      </div>
      {/* Montagm do layout ou de login ou do cadastro  */}
      <AccountContext.Provider value={contextValue}>
        <BoxContainer>
          <TopContainer>
            <BackDrop
              initial={false}
              animate={isExpanded ? "expanded" : "collapsed"}
              variants={backdropVariants}
              transition={expandingTransition}
            />
            {active === "signin" && (
              <HeaderContainer>
                <HeaderText>Bem Vindo</HeaderText>
                <SmallText>Faça o login para continuar!</SmallText>
              </HeaderContainer>
            )}
            {active === "signup" && (
              <HeaderContainer>
                <HeaderText>Crie uma</HeaderText>
                <HeaderText>Conta</HeaderText>
                <SmallText>Faça o login para continuar!</SmallText>
              </HeaderContainer>
            )}
          </TopContainer>
          <InnerContainer>
            {active === "signin" && <LoginForm handleSubmit={handleSubmit} />}
            {active === "signup" && (
              <SignupForm handleSubmit={handleRegistroSubmit} />
            )}
          </InnerContainer>
        </BoxContainer>
      </AccountContext.Provider>
    </>
  );
}
